import React, { Component } from "react";
import "./product.css";
import { connect } from "react-redux";
// @Functions
import changeToSlug from "../../utils/ChangeToSlug";
// @ComponentS
import {
  CButton,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
} from "@coreui/react";
// @Actions
import ProductsActions from "../../redux/actions/products";
import OperationActions from "../../redux/actions/operations";
import ColorActions from "../../redux/actions/color";
import CategoryActions from "../../redux/actions/categories";

class ProductDetail extends Component {
  constructor(props) {
    super(props);
    const { product, listCategories, listBrands } = props;
    this.state = {
      // @Product Info
      id: product ? product._id : "",
      name: product ? product.name : "",
      price: product ? product.price : "",
      pathseo: product ? product.pathseo : "",
      amount: product ? product.amount : "",
      warrently: product ? product.warrently : "",
      category: product ? product.category : listCategories[0]._id,
      brand: product ? product.brand : listBrands[0]._id,
      bigimage: product ? product.bigimage : "",
      image: product ? product.image : [],
      specifications: product ? (product.specifications.length!=0 ? product.specifications
        : listCategories[listCategories.findIndex(i => i._id === product.category)].specifications)
      : listCategories[0].specifications,

      // @Product Image
      previewSource: "",
      selectedFile: "",
      fileInputState: "",
      // @Product list images
      previewList: [],
      selectedList: [],
      fileInputList: [],
      value : product && this.setValue()
    };

  }

  setValue = () => {
    var value = [];
    const {specifications} = this.state;
    specifications.map(item =>{
      if(item._id){
        value.push({
          _id: item._id,
          value: item.value
        })
      }
      else{
        value.push({
          _id: item,
          value: ""
        })
      }
    })
    return value;
  }

  componentWillMount() {
    const { onGetListOperation, onGetListColor } = this.props;
    const {value} = this.state;
    onGetListOperation();
    onGetListColor();
    console.log("value", value)
  }

  onChange = (event) => {
    var target = event.target;
    var name = target.name;
    var value = target.value;
    this.setState({
      [name]: value,
    });
  };

  deletePhoto = (id) => {
    const { image } = this.state;
    // Vị trí trong mảng có image cần xóa
    var deleteIndex = image.indexOf(image.find((img) => img._id === id));
    // Tạo mảng mới không có phần tử muốn xóa
    image.splice(deleteIndex, 1);
    this.setState({
      image,
    });
  };

  deletePreview = (item) => {
    const { previewList } = this.state;
    // Vị trí trong mảng có image cần xóa
    var deleteIndex = previewList.indexOf(
      previewList.find((img) => img === item)
    );
    // Tạo mảng mới không có phần tử muốn xóa
    previewList.splice(deleteIndex, 1);
    this.setState({
      previewList,
    });
  };

  handleFileInputChange = (e) => {
    const file = e.target.files[0];
    // 1. Hiển thị ảnh vừa thêm
    this.previewFile(file);
    this.setState({
      selectedFile: file,
      fileInputState: e.target.value,
    });
  };

  handleListInputChange = (e) => {
    const file = e.target.files[0];
    const { selectedList, fileInputList } = this.state;
    // 1. Hiển thị ảnh vừa thêm
    this.previewList(file);
    selectedList.push(file);
    fileInputList.push(e.target.value);
    this.setState({
      selectedList,
      fileInputList,
    });
  };

  // Hàm xử lý file - set hiển thị ảnh mới thêm vào state previewSource
  previewFile = (file) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      this.setState({
        previewSource: reader.result,
      });
    };
  };

  // Hàm xử lý file - set hiển thị ảnh mới thêm vào state previewSource
  previewList = (file) => {
    const reader = new FileReader();
    const { previewList } = this.state;
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      previewList.push(reader.result);
      this.setState({
        previewList,
      });
    };
  };

  onSelect = (event) => {
    var target = event.target;
    var name = target.name;
    var id = target.value;
    const { onGetDetailCategories } = this.props;
    this.setState({
      [name]: id,
    });
    onGetDetailCategories(id);
  };

  onChangeDetail = (event) =>{
    var target = event.target;
    var name = target.name;
    var _value = target.value;
    const {value} = this.state;
    if(value.findIndex(obj => obj._id === name) == -1){
      this.setState((prevState) => ({
        value: [...prevState.value,
        {
          _id:name,
          value:_value
        }]
      }))
    }
    else{
      this.setState((prevState) => ({
        value: prevState.value.map(
          obj => (obj._id === name ? Object.assign(obj, { value: _value }) : obj)
        )
      }))
    }
  }

  componentDidUpdate(props) {
    const {product, categoryDetail} =this.props;
    if (categoryDetail !== props.categoryDetail && categoryDetail) {
      if(categoryDetail._id == product.category){
        if(product.specifications.length>0){
          this.setState({
            specifications: product.specifications,
          });
        }
        else{
          this.setState({
            specifications: categoryDetail.specifications,
          });
        }
      }
      else{
        this.setState({
          specifications: categoryDetail.specifications,
        });
      }
    }
  }

  onSubmitImage = async () => {
    const { selectedFile, selectedList, id } = this.state;
    // @Condition cloudinary là FormData()
    // @Xử lý ảnh trước khi lưu
    // Nếu có import thumbnail mới thì
    if (selectedFile && selectedList.length !== 0) {
      var formData1 = new FormData();
      formData1.append("image", selectedFile);
      await this.setState({
        bigimage: formData1,
      });
      var formData2 = new FormData();
      for (var i = 0; i < selectedList.length; i++) {
        formData2.append("image", selectedList[i]);
      }
      this.onCallback(id, formData2);
    } else if (selectedFile) {
      // 1. Lưu cloudinary
      // eslint-disable-next-line
      var reader = new FileReader();
      reader.readAsDataURL(selectedFile);
      // 2. Lưu state dạng FormData
      reader.onloadend = () => {
        // eslint-disable-next-line
        var formData1 = new FormData();
        formData1.append("image", selectedFile);
        this.setState({
          bigimage: formData1,
        });
        this.onCallback(id);
      };
      reader.onerror = () => {
        console.error("Lỗi không thêm mới được Thumbnail product");
      };
    } else if (selectedList.length !== 0) {
      // 1. Lưu cloudinary
      // eslint-disable-next-line
      var formData2 = new FormData();
      for (var i = 0; i < selectedList.length; i++) {
        formData2.append("image", selectedList[i]);
      }
      this.onCallback(id, formData2);
    } else {
      this.onCallback(id, null);
    }
    // @Xử lý các thông tin khác
    // 3. Update data
  };

  onCallback = (id, formData) => {
    const { onCreate, onUpdateImage } = this.props;
    const {
      name,
      price,
      amount,
      warrently,
      category,
      brand,
      bigimage,
      image,
      pathseo,
      value,
      specifications
    } = this.state;
    if (id) {
      // eslint-disable-next-line
      var data = {
        name,
        price,
        amount,
        warrently,
        category,
        brand,
        bigimage,
        image,
        pathseo,
        specifications: value,
      };
      onUpdateImage(id, data, formData);
    } else {
      // 4. Create data
      // eslint-disable-next-line
      var data = {
        name,
        price,
        amount,
        warrently,
        category,
        brand,
        bigimage,
        pathseo,
        image,
        specifications:value,
      };
      onCreate(data, formData);
    }
  };

  setSpecification = (specification) =>{
    const {listSpecification} = this.props;
    const specificationName = listSpecification.find(obj => obj._id === specification);
    return specificationName.name;
  }

  render() {
    const {
      name,
      price,
      amount,
      pathseo,
      warrently,
      category,
      brand,
      bigimage,
      image,
      previewSource,
      fileInputState,
      previewList,
      specifications,
    } = this.state;
    const {
      large,
      onClose,
      listCategories,
      listBrands,
      product,
    } = this.props;
    return (
      <CModal show={large} onClose={() => onClose(!large)} size="lg">
        <CModalHeader closeButton>
          <CModalTitle>
            {product ? "Sửa thông tin sản phẩm" : "Thêm sản phẩm mới"}
          </CModalTitle>
        </CModalHeader>
        <CModalBody>
          <div className="row">
            <div className="col-12 col-lg-6">
              <form>
                <div className="form-group">
                  <label>Tên sản phẩm:</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={name}
                    onChange={this.onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Giá bán lẻ (VND):</label>
                  <input
                    type="number"
                    className="form-control"
                    name="price"
                    value={price}
                    onChange={this.onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Slug:</label>
                  {name ? (
                    <input
                      type="text"
                      className="form-control"
                      name="pathseo"
                      value={pathseo ? pathseo : changeToSlug(name)}
                      onChange={this.onChange}
                    />
                  ) : (
                    <input
                      type="text"
                      className="form-control"
                      name="pathseo"
                      value={pathseo}
                      onChange={this.onChange}
                    />
                  )}
                </div>
                <div className="form-group">
                  <label>Số lượng (chiếc):</label>
                  <input
                    type="number"
                    className="form-control"
                    name="amount"
                    value={amount}
                    onChange={this.onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Thời hạn bảo hành:</label>
                  <input
                    type="number"
                    className="form-control"
                    name="warrently"
                    value={warrently}
                    onChange={this.onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Loại sản phẩm:</label>
                  <select
                    className="form-control"
                    required="required"
                    name="category"
                    value={category}
                    onChange={this.onSelect}
                  >
                    {listCategories.map((category, index) => {
                      return (
                        <option key={index} value={category._id}>
                          {category.name}
                        </option>
                      );
                    })}
                  </select>
                </div>
                <div className="form-group">
                  <label>Tên thương hiệu:</label>
                  <select
                    className="form-control"
                    required="required"
                    name="brand"
                    value={brand}
                    onChange={this.onChange}
                  >
                    {listBrands.map((brand, index) => {
                      return (
                        <option key={index} value={brand._id}>
                          {brand.name}
                        </option>
                      );
                    })}
                  </select>
                </div>
                {bigimage ? (
                  <div className="form-group img-thumbnail3">
                    {previewSource ? (
                      <img src={previewSource} className="w-100" alt="" />
                    ) : (
                      <img
                        src={bigimage.public_url}
                        style={{ border: "1px solid", width: "100%" }}
                        alt=""
                      />
                    )}
                    <div className="file btn btn-lg btn-primary">
                      Change Photo
                      <input
                        type="file"
                        name="image"
                        id="fileInput"
                        value={fileInputState}
                        onChange={this.handleFileInputChange}
                        style={{ width: "100%" }}
                      />
                    </div>
                  </div>
                ) : (
                  <div className="form-group img-thumbnail3">
                    {previewSource ? (
                      <img src={previewSource} className="w-100" alt="" />
                    ) : (
                      <img
                        src="https://www.allianceplast.com/wp-content/uploads/2017/11/no-image.png"
                        alt=""
                        style={{ border: "1px solid", width: "100%" }}
                      ></img>
                    )}
                    <div className="file btn btn-lg btn-primary">
                      Change Photo
                      <input
                        type="file"
                        name="image"
                        id="fileInput"
                        value={fileInputState}
                        onChange={this.handleFileInputChange}
                        style={{ width: "100%" }}
                      />
                    </div>
                  </div>
                )}
                <div className="form-group">
                  <div className="row">
                    {image &&
                      image.map((item, index) => {
                        return (
                          <div className="col-3" key={index}>
                            <div className=" img-thumbnail2">
                              <img
                                src={item.public_url}
                                className="w-100"
                                style={{ border: "1px solid" }}
                                alt=""
                              ></img>
                              <div className="btn btn-lg btn-primary img-des">
                                Delete Photo
                                <input
                                  type="button"
                                  name="image"
                                  className="w-100 h-100"
                                  onClick={() => this.deletePhoto(item._id)}
                                />
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    {previewList[0] && (
                      <>
                        {previewList.map((item, index) => {
                          return (
                            <div className="col-3" key={index}>
                              <div className="img-thumbnail2">
                                <img src={item} alt="" className="w-100" />
                                <div className="btn btn-lg btn-primary img-des">
                                  Delete Photo
                                  <input
                                    type="button"
                                    name="image"
                                    className="w-100 h-100"
                                    onClick={() => this.deletePreview(item)}
                                  />
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </>
                    )}
                    <div className="col-3">
                      <div className=" img-thumbnail2">
                        <img
                          src="https://www.allianceplast.com/wp-content/uploads/2017/11/no-image.png"
                          alt=""
                          className="w-100"
                          style={{ border: "1px solid" }}
                        ></img>
                        <div className="btn btn-lg btn-primary img-des">
                          Add Photo
                          <input
                            type="file"
                            onChange={this.handleListInputChange}
                            className="w-100 h-100"
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </form>
            </div>
            <div className="col-12 col-lg-6">
              <div className="card text-white mb-3">
                <div className="card-header bg-primary">Chi tiết sản phẩm</div>
                <div className="card-body text-dark">
                  {specifications[0] && specifications[0].value
                    ?
                    specifications.map((item, index) => {
                    return (
                    <div className="form-group" key={index}>
                      <label key={index+1}>{item.name}</label>
                      <input
                        type="text"
                        className="form-control"
                        key={index}
                        name={item._id}
                        defaultValue={item.value}
                        onChange={this.onChangeDetail}
                      />
                    </div>)
                    })
                    : specifications.map((item, index) => {
                    return (
                    <div className="form-group" key={index}>
                      <label>{this.setSpecification(item)}</label>
                      <input
                        type="text"
                        className="form-control"
                        name={item}
                        onChange={this.onChangeDetail}
                      />
                    </div>)
                  })}
                </div>
              </div>
            </div>
          </div>
        </CModalBody>
        <CModalFooter>
          <CButton color="success" onClick={() => this.onSubmitImage(!large)}>
            Lưu ảnh
          </CButton>{" "}
          <CButton color="secondary" onClick={() => onClose(!large)}>
            Cancel
          </CButton>
        </CModalFooter>
      </CModal>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    productDetail: state.products.detail,
    listBrands: state.brands.list,
    listCategories: state.categories.list,
    listOperations: state.operations.list,
    listColor: state.color.list,
    categoryDetail: state.categories.detail,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onCreate: (params, formData) => {
      dispatch(ProductsActions.onCreate({ params, formData }));
    },
    onUpdateImage: (id, params, formData) => {
      dispatch(ProductsActions.onUpdateImage({ id, params, formData }));
    },
    onGetListColor: () => {
      dispatch(ColorActions.onGetList());
    },
    onGetListOperation: () => {
      dispatch(OperationActions.onGetList());
    },
    onGetDetailCategories: (id) => {
      dispatch(CategoryActions.onGetDetail(id));
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductDetail);
