import React, { Component } from 'react'
import { connect } from "react-redux";
// @Components
import {
  CButton,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
} from "@coreui/react";
// @Actions
import ProductsActions from "../../redux/actions/products";

const fields = ['name' ,{ key: 'actions', _style: { width: '10%'} }]

class Group extends Component {
  constructor(props){
    super(props);
    const {group} = props
    this.state = {
      keyword: "",
      listProduct: group.products ? group.products : []
    }
  }

  handleFilter = (event) => {
    const { keyword } = this.state;
    const { onFilter } = this.props;
		var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
      [name]:  value
    })
    onFilter(keyword);
	}

  render() {
    const {modal, onCloseModal, group, listSearch} = this.props;
    const {keyword, listProduct} = this.state;
    return (
      <CModal show={modal} onClose={() => onCloseModal("_modal",!modal)} closeOnBackdrop={false}>
        <CModalHeader closeButton>
          <CModalTitle>
            Nhóm sản phẩm {group._id}
          </CModalTitle>
        </CModalHeader>
        <CModalBody>
          <input className="form-control" name="keyword" value={keyword} onChange={this.handleFilter}></input>
          <div className="card mb-0">
          {listSearch && keyword && listSearch.map((product, index) =>{
            return (
              <div key={index}>
                <div className="row">
                  <div className="col-12">
                    <p className="my-1 mx-2">{product.name}</p>
                  </div>
                </div>
                <div className="border-bottom"></div>
              </div>
            )
          })}
          {listProduct && <CDataTable
            items={listProduct}
            fields={fields}
            striped
            itemsPerPage={5}
            pagination
            scopedSlots = {{
              'name': (item) => (
                <td>{this.setSpecification(item)}</td>
              ),
              'actions': (item) => (
                <CButton
                  onClick={() => this.onDeleteSpecification(item)}
                  className="mr-1 mb-1 mb-xl-0"
                  color="danger"
                >
                  Xoá
                </CButton>
              )
            }}
          />}
          </div>
        </CModalBody>
        <CModalFooter>
          <CButton color="secondary" onClick={() => onCloseModal("_modal",!modal)}>
            Cancel
          </CButton>
        </CModalFooter>
      </CModal>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listSearch: state.products.listSearch,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFilter : (keyword) =>{
      dispatch(ProductsActions.onFilter(keyword))
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Group);
