import React, { Component } from 'react'
import { connect } from "react-redux";
// @Components
import {
  CButton,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
  CDataTable
} from "@coreui/react";
// @Actions
import ProductsActions from "../../redux/actions/products";
// @Functions
import {INITIAL_IMAGE} from '../../constants';

const fields = ['ảnh', 'tên hiển thị', 'tên thật', { key: 'actions', _style: { width: '15%'} }]

class Group extends Component {
  constructor(props){
    super(props);
    const {group} = props
    this.state = {
      keyword: "",
      name: "",
      product: null,
      editing: false,
      show: false,
      listProduct: group.products ? group.products : []
    }
  }

  handleFilter = (event) => {
    const { keyword } = this.state;
    const { onFilter } = this.props;
		var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
      [name]:  value
    })
    onFilter(keyword);
  }

  onChange = (event) => {
    var target = event.target;
    var name = target.name;
    var value = target.value;
    this.setState({
      [name]: value,
    });
  };

  onDelete = (item) =>{
    const {listProduct} = this.state;
    listProduct.splice(listProduct.indexOf(item), 1);
    this.setState({
      listProduct
    })
  }

  render() {
    const {modal, onCloseModal, group, listSearch} = this.props;
    const {keyword, listProduct, name, product, show} = this.state;
    return (
      <CModal show={modal} onClose={() => onCloseModal("_modal",!modal)} closeOnBackdrop={false} size="lg">
        <CModalHeader closeButton>
          <CModalTitle>
            Nhóm sản phẩm {group._id}
          </CModalTitle>
        </CModalHeader>
        <CModalBody>
          <div className="row">
          <div className="col-12 col-md-6">
              {listProduct && <CDataTable
                items={listProduct}
                fields={fields}
                striped
                itemsPerPage={5}
                pagination
                scopedSlots = {{
                  'ảnh':
                    (item) => (
                      <td>
                        <img src={ item.product.bigimage ? item.product.bigimage.public_url : INITIAL_IMAGE } style={{height:'10vh'}} alt={item.product.name} />
                      </td>
                    ),
                  'tên hiển thị': (item) => (
                    <td>{item.name}</td>
                  ),
                  'tên thật': (item) => (
                    <td>{item.product.name}</td>
                  ),
                  'actions': (item) => (
                    <td>
                    <CButton
                      onClick={() => this.setState({
                        editing: true,
                        name: item.name,
                        product: item.product
                      })}
                      className="mr-1 mb-1 mb-xl-0"
                      color="warning"
                    >
                      <i className="fa fa-highlighter"></i>
                    </CButton>
                    <CButton
                      onClick={() => this.onDelete(item)}
                      className="mr-1 mb-1 mb-xl-0"
                      color="danger"
                    >
                      <i className="fa fa-trash-alt"></i>
                    </CButton>
                    </td>
                  )
                }}
              />}
            </div>
            <div className="col-12 col-md-6">
              {show && <div className="card">
                <div className="card-header bg-primary">
                  <p className="text-white mb-0">Chi tiết</p>
                </div>
                <div className="card-body">
                  <div className="form-group">
                    <label>Tên hiển thị:</label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      value={name}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>Sản phẩm:</label>
                    <input className="form-control" name="keyword" value={keyword} onChange={this.handleFilter}></input>
                    <div className="card mb-0">
                      {listSearch && keyword && listSearch.map((product, index) =>{
                        return (
                          <div key={index}>
                            <div className="row text-dark text-decoration-none" style={{height: "80px"}} key={index}>
                              <div className="col-3 my-auto">
                                <><img style={{height: "80px"}} src={product.bigimage.public_url}></img></>
                              </div>
                              <div className="col-6 text-left my-auto">
                                <p className="mb-0">{product.name}</p>
                              </div>
                              <div className="col-3 align-self-center">
                                <button type="button" className="btn btn-success" onClick={() => this.setState({product, keyword: ""})}><i className="fa fa-folder-plus"></i></button>
                              </div>
                            </div>
                            <div className="border-bottom"></div>
                          </div>
                        )
                      })}
                      {product &&
                      <div className="card mb-0">
                        <div className="row text-dark text-decoration-none" style={{height: "80px"}}>
                          <div className="col-3 my-auto">
                            <><img style={{height: "80px"}} src={product.bigimage.public_url}></img></>
                          </div>
                          <div className="col-9 text-left my-auto">
                            <p className="mb-0">{product.name}</p>
                          </div>
                        </div>
                      </div>}
                    </div>
                  </div>
                </div>
                <div className="card-footer">
                  <CButton color="success">Lưu</CButton>{" "}
                  <CButton color="secondary" onClick={()=>this.setState({
                    editing: false,
                    name: "",
                    product: null,
                    show: false
                  })}>Hủy</CButton>
                </div>
              </div>}
            </div>
          </div>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary" /* onClick={() => onCloseModal("_modal",!modal)} */>
            Save group
          </CButton>{" "}
          <CButton color="secondary" onClick={() => onCloseModal("_modal",!modal)}>
            Cancel
          </CButton>
        </CModalFooter>
      </CModal>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listSearch: state.products.listSearch,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFilter : (keyword) =>{
      dispatch(ProductsActions.onFilter(keyword))
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Group);
