import React, { Component } from 'react';
import { CButton, CModal, CModalBody, CModalFooter, CModalHeader, CModalTitle } from '@coreui/react';

class OrderDetail extends Component {
  constructor(props){
    super(props);
    const {order} = props;
    this.state = {
      id: order ? order._id : '',
      total_price: order ? order.total_price: ''
    }
  }

  onChange = (event) =>{
    var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
        [name]:  value
    })
  }

	render() {
    const {total_price, } = this.state;
    const { large, onClose, setImage, listCategories, listBrands, product} = this.props;
    return (
			<CModal show={large} onClose={() => onClose(!large)} size="lg">
				<CModalHeader closeButton>
        <CModalTitle>{product ? "Sửa thông tin sản phẩm" : "Thêm sản phẩm mới"}</CModalTitle>
				</CModalHeader>
				<CModalBody>
					<div className="row">
						<div className="col-12 col-lg-6">
							<form>
								<div className="form-group">
									<label>Tổng hóa đơn: (VND)</label>
                  <input type="text" className="form-control" name="total_price" value={total_price} onChange={this.onChange}/>
								</div>

							</form>
						</div>
            <div className="col-12 col-lg-6">
							<form>
								<div className="form-group">
                  <img src={setImage(bigimage)} style={{border: '1px solid'}} alt=""></img>
                  <input type="file" className="form-control" name="image"/>
								</div>
                <div className="form-group">
                  <div className="row">
                  {image && image.map((item, index) => {
                    return (
                      <div className="col-4" key={index}>
                      <img src={setImage(item)} className="w-100" style={{border: '1px solid'}} alt=""></img>
                      </div>
                    )
                  })}
                  </div>
								</div>
							</form>
						</div>
					</div>
				</CModalBody>
				<CModalFooter>
					<CButton color="primary" onClick={() => this.onSubmit(!large)}>
						Save
					</CButton>{' '}
					<CButton color="secondary" onClick={() => onClose(!large)}>
						Cancel
					</CButton>
				</CModalFooter>
			</CModal>
		);
	}
}

export default OrderDetail;
