import { takeEvery, fork, all, call, put } from "redux-saga/effects";
import { get } from "lodash";
import SelectorActions, { SelectorActionTypes } from "../actions/selector";
import { addSelector, deleteSelector } from "../apis/selector";

/**
 *
 * create
 */
function* handleCreate({ payload }) {
  try {
    const result = yield call(addSelector, payload.params);
    const data = get(result, "data", {});
    if (data.code !== 201) throw data;
    yield put(SelectorActions.onCreateSuccess(data.selector));
    yield put(SelectorActions.onGetList());
  } catch (error) {
    yield put(SelectorActions.onCreateError(error));
  }
}

/**
 *
 * delete
 */

function* handleDelete({ id }) {
  try {
    const result = yield call(deleteSelector, id);
    const data = get(result, "data", {});
    if (data.code !== 200) throw data;
    yield put(SelectorActions.onDeleteSuccess(data));
    yield put(SelectorActions.onGetList());
  } catch (error) {
    yield put(SelectorActions.onDeleteError(error));
  }
}

export function* watchCreate() {
  yield takeEvery(SelectorActionTypes.CREATE, handleCreate);
}
export function* watchDelete() {
  yield takeEvery(SelectorActionTypes.DELETE, handleDelete);
}

export default function* rootSaga() {
  yield all([
    fork(watchCreate),
    fork(watchDelete),
  ]);
}
