import React, { Component }  from 'react'
import { get } from "lodash";
import { connect } from "react-redux";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
// @Components
import { Button, UncontrolledPopover, PopoverHeader, PopoverBody, Collapse } from 'reactstrap';
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CButton,
  CRow,
} from '@coreui/react'
import OrderDetail from './OrderDetail'
import OrderActions from "../../redux/actions/order";

const fields = ['Date of create', 'Phone','Payment Status','Bill Status','Payment Method','Total','actions']

class PopoverContent extends Component{
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false
    }
  }

  render () {
    const {product} = this.props;
    const {isOpen} = this.state;
    return (
      <>
        <PopoverHeader>{product}</PopoverHeader>
        <PopoverBody>
          <Button>Click me</Button>
        </PopoverBody>
      </>
    );
  }
}

class OrderList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      large: false,
    }
  }
  componentDidMount() {
    const { onClearState, onGetList } = this.props;
    onClearState();
    onGetList();
  }

  submit = (id) => {
    confirmAlert({
      title: 'Thông báo',
      message: 'Bạn có thực sự muốn xóa brand này?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => this.onDelete(id)
        },
        {
          label: 'No'
        }
      ]
    });
  };
  setLarge = (large) => {
    this.setState({
      large
    })
  }

  onDelete = (_id)=>{
    const {onDelete} = this.props;
    onDelete(_id);
  }

  onUpdate = (large, item) =>{
    const { onGetDetail } = this.props;
    this.setState({
      large
    })
    if(item){onGetDetail(item)}
  }

  onClose = (large) =>{
    const { onClearDetail } = this.props;
    this.setState({
      large
    })
    onClearDetail();
  }

  render () {
    const {large} = this.state;
    const {listOrder, orderDetail, onClearDetail} = this.props;
    return (
      <>
        <CRow>
          <CCol>
            <CCard>
              <CCardHeader>
                <h5 className="float-left my-2">Danh sách đơn hàng</h5>
              </CCardHeader>

              <CCardBody>
                <CDataTable
                  items={listOrder}
                  fields={fields}
                  hover
                  striped
                  bordered
                  itemsPerPage={10}
                  pagination
                  scopedSlots = {{
                    'Date of create': (item) => (
                      <td>{item.createdAt}</td>
                    ),
                    'Phone': (item) => (
                      <td>{item.shipping_phonenumber}</td>
                    ),
                    'Payment Method': (item) => (
                      <td>{item.payment_method=== "local" ? '(COD) Tiền mặt' : 'Paypal'}</td>
                    ),
                    'Payment Status': (item) => (
                      <td>{item.is_paid== true ? 'Đã thanh toán' : 'Chưa thanh toán'}</td>
                    ),
                    'Bill Status': (item) => (
                      <td>{item.status== true ? 'Đã giao hàng' : 'Chưa giao hàng'}</td>
                    ),
                    'Total': (item) => (
                      <td>{item.total_price}</td>
                    ),
                    'actions':
                    (item)=>(
                      <>
                      <Button id="ScheduleUpdateButton" type="button">
                        Các tác vụ
                      </Button>
                      <UncontrolledPopover trigger="click" placement="top" target="ScheduleUpdateButton">
                          <PopoverContent product={item._id} />
                      </UncontrolledPopover>
                      </>
                      /* {
                        <td>
                        <CButton
                          onClick={() => this.onUpdate(!large, item._id)}
                          className="mr-1 mb-1 mb-xl-0"
                          color="warning"
                        >
                          Sửa
                        </CButton>
                        <CButton
                          onClick={() => this.submit(item._id)}
                          className="mr-1"
                          color="danger"
                        >
                          Hủy đơn
                        </CButton>
                      </td> }*/)
                  }}
                />
                {(orderDetail && large) && <OrderDetail large={large} order={orderDetail} onClose={this.onClose}
                onClearDetail={onClearDetail}
                />}

                {(!orderDetail && large) && <OrderDetail large={large} order={orderDetail} onClose={this.onClose}
                onClearDetail={onClearDetail}
                />}
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listOrder: state.order.list,
    orderDetail: state.order.detail,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onGetList: () => {
      dispatch(OrderActions.onGetList())
    },
    onClearState: () =>{
      dispatch(OrderActions.onClearState())
    },
    onClearDetail: () =>{
      dispatch(OrderActions.onClearDetail())
    },
    onGetDetail: (id) => {
      dispatch(OrderActions.onGetDetail(id))
    },
    onDelete: (id) =>{
      dispatch(OrderActions.onDelete({id}))
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(OrderList)
