}
export function* watchUpdate() {
  yield takeEvery(ProductsActionTypes.UPDATE, handleUpdate);
}
export function* watchDelete() {
  yield takeEvery(ProductsActionTypes.DELETE, handleDelete);
} */

export default function* rootSaga() {
  yield all([
    fork(watchGetList),
    /* fork(watchGetDetail),
    fork(watchCreate),
    fork(watchUpdate),
    fork(watchDelete), */
  ]);
}
