//Nơi chứa các hàm gọi API riêng biệt cho từng module
//Module Product
import axiosService from '../../utils/AxiosService';
import {API_ENDPOINT_AUTH} from '../../constants/index';

const url = 'products/selectors';
// http://localhost:3000/products/selectors  METHOD = POST
export const addSelector = (data) =>{
  return axiosService.post(`${API_ENDPOINT_AUTH}${url}`, data);
};

// http://localhost:3000/products/selectors/:selectorID  METHOD = DELETE
export const deleteSelector  = (selectorID) =>{
  return axiosService.delete(`${API_ENDPOINT_AUTH}${url}/${selectorID}`);
}
