import React, { Component } from 'react';
import { CButton, CModal, CModalBody, CModalFooter, CModalHeader, CModalTitle } from '@coreui/react';

class ProductDetail extends Component {
  constructor(props){
    super(props);
    const {product} = props;
    this.state = {
      name: product.name,
      price: product.price,
      amount: product.amount,
      warrently: product.warrently,
      category: product.category,
      brand: product.brand,
      bigimage: product.bigimage,
      image: product.image,
    }
  }

  onChange = (event) =>{
    var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
        [name]:  value
    })
  }
	render() {
    const {name, price, amount, warrently, category, brand, bigimage, image} = this.state;
    const { large, setLarge, setImage, setBrand, setCategory, listCategories } = this.props;
		return (
			<CModal show={large} onClose={() => setLarge(!large)} size="lg">
				<CModalHeader closeButton>
        <CModalTitle>Sửa thông tin sản phẩm</CModalTitle>
				</CModalHeader>
				<CModalBody>
					<div className="row">
						<div className="col-12 col-lg-6">
							<form>
								<div className="form-group">
									<label for="">Tên sản phẩm:</label>
                  <input type="text" className="form-control" name="name" value={name} onChange={this.onChange}/>
								</div>
								<div className="form-group">
									<label for="">Giá bán lẻ (VND):</label>
                  <input type="number" className="form-control" name="price" value={price} onChange={this.onChange}/>
								</div>
                <div className="form-group">
									<label for="">Số lượng (chiếc):</label>
                  <input type="number" className="form-control" name="amount" value={amount} onChange={this.onChange}/>
								</div>
                <div className="form-group">
									<label for="">Thời hạn bảo hành:</label>
                  <input type="number" className="form-control" name="warrently" value={warrently} onChange={this.onChange}/>
								</div>
                <div className="form-group">
									<label for="">Loại sản phẩm:</label>
                  <select className="form-control" required="required" name="category"
                        value={setCategory(category)}
                        onChange={this.onChange}>
                    {listCategories.map((index, category) =>{
                      return(
                        <option key={index} value={category._id}>{category.name}</option>
                      )
                    })}
                  </select>
								</div>
                <div className="form-group">
									<label for="">Tên thương hiệu:</label>
                  <input type="text" className="form-control" name="brand" value={setBrand(brand)} onChange={this.onChange}/>
								</div>
							</form>
						</div>
            <div className="col-12 col-lg-6">
							<form>
								<div className="form-group">
                  <img src={setImage(bigimage)} style={{border: '1px solid'}}></img>
                  <input type="file" className="form-control" name="image"/>
								</div>
                <div className="form-group">
                  <div className="row">
                  {image.map((item, index) => {
                    return (
                      <div className="col-4">
                      <img src={setImage(item)} className="w-100" style={{border: '1px solid'}}></img>
                      </div>
                    )
                  })}
                  </div>
								</div>
							</form>
						</div>
					</div>
				</CModalBody>
				<CModalFooter>
					<CButton color="primary" onClick={() => setLarge(!large)}>
						Save
					</CButton>{' '}
					<CButton color="secondary" onClick={() => setLarge(!large)}>
						Cancel
					</CButton>
				</CModalFooter>
			</CModal>
		);
	}
}

export default ProductDetail;
