  componentDidMount() {
    const { onGetListColor } = this.props;
    onGetListColor();
  }

  onChange = (event) => {
    var target = event.target;
    var name = target.name;
    var value = target.value;
    this.setState({
      [name]: value,
    });
  };

  deletePhoto = (id) => {
    const { image } = this.state;
    // Vị trí trong mảng có image cần xóa
    var deleteIndex = image.indexOf(image.find((img) => img._id === id));
    // Tạo mảng mới không có phần tử muốn xóa
    image.splice(deleteIndex, 1);
    this.setState({
      image,
    });
  };

  deletePreview = (item) => {
    const { previewList, selectedList } = this.state;
    // Vị trí trong mảng có image cần xóa
    var deleteIndex = previewList.indexOf(
      previewList.find((img) => img === item)
    );
    // Tạo mảng mới không có phần tử muốn xóa
    previewList.splice(deleteIndex, 1);
    selectedList.splice(deleteIndex, 1);
    this.setState({
      previewList, selectedList
    });
  };

  handleFileInputChange = (e) => {
    var {name} = e.target;
    const file = e.target.files[0];
    // 1. Hiển thị ảnh vừa thêm
    this.previewFile(file, name);
    if(name === "previewSource"){
      this.setState({
        selectedFile: file,
      });
    }
  };
  handleListInputChange = (e) => {
    const file = e.target.files[0];
    const { selectedList } = this.state;
    // 1. Hiển thị ảnh vừa thêm
    this.previewList(file);
    selectedList.push(file);
    this.setState({
      selectedList,
    });
  };

  // Hàm xử lý file - set hiển thị ảnh mới thêm vào state previewSource
  previewFile = (file, name) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      this.setState({
        [name]: reader.result,
      });
    };
  };
  // Hàm xử lý file - set hiển thị ảnh mới thêm vào state previewSource
  previewList = (file) => {
    const reader = new FileReader();
    const { previewList } = this.state;
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      previewList.push(reader.result);
      this.setState({
        previewList,
      });
    };
  };

  onSelect = (event) => {
    var target = event.target;
    var name = target.name;
    var id = target.value;
    const { onGetDetailCategory } = this.props;
    this.setState({
      [name]: id,
    });
    onGetDetailCategory(id);
  };

  onChangeDetail = (event) => {
    var target = event.target;
    var name = target.name;
    var value = target.value;
    this.setState((prevState) => ({
      specifications: prevState.specifications.map((obj) =>
        obj._id === name ? Object.assign(obj, { value }) : obj
      ),
    }));
  };

  onChangeDetailSelect = (value, action) => {
    var _value = []
    value.map(item => {
      _value.push(item.value)
    })
    this.setState((prevState) => ({
      specifications: prevState.specifications.map((obj) =>
        obj._id === action.name ? Object.assign(obj, { value: JSON.stringify(_value) }) : obj
      ),
    }));
    console.log(value, action.name)
  };

  onAddColor = (value) => {
    if (value === "none") {
      this.setState({
        colorEditing: "inline-flex",
        btnStatus: "Hủy",
        onEditing: false,
      });
    } else {  //Click button Hủy
      this.setState({
        // Gán giá trị button
        colorEditing: "none",
        btnStatus: "Thêm màu",
        // Gán giá trị fields
        nameColor: "",
        amountColor: 0,
        priceColor: 0,
        realPriceColor: 0,
        onEditing: false,
        imageColor: INITIAL_IMAGE,
        // Trả preview về mặc định
        previewColorImage: "",
        selectedColor: "",
      });
    }
  };

  onEditColor = (item) => {
    const { colors } = this.state;
    this.setState({
      // Gán giá trị button
      colorEditing: "inline-flex",
      btnStatus: "Hủy",
      // Gán giá trị fields
      nameColor: item._id,
      amountColor: item.amount ? item.amount : 0,
      priceColor: item.price ? item.price : 0,
      realPriceColor: item.real_price ? item.real_price : 0,
      onEditing: true,
      imageID: item.image,
      imageColor: item.image_link ? item.image_link :  INITIAL_IMAGE,
      indexColor: colors.indexOf(item),
      // Trả preview về mặc định
      previewColorImage: "",
      selectedColor: "",
    });
  };

  onDeleteColor = (item) => {
    const { colors } = this.state;
    colors.splice(colors.indexOf(item), 1);
    this.setState({
      colors,
    });
  };

  onSaveColor() {
    const {nameColor,
      priceColor,
      realPriceColor,
      amountColor,
      onEditing,
      indexColor,
      colors,
      imageID,
      imageColor,
      selectedColorImage,
      previewColorImage
    } = this.state;
    const { listColor } = this.props;
    if(!nameColor){
      toastError("Chọn màu trước khi lưu");
    }
    else{
      if (onEditing === false) {
        // TH thêm màu
        if (colors.find((obj) => obj._id === nameColor)) {
          toastError("Màu này đã tồn tại");
        } else {
          colors.push({
            _id: nameColor,
            name_vn: listColor.find((obj) => obj._id === nameColor).name_vn,
            amount: amountColor,
            price: priceColor,
            real_price: realPriceColor,
            image: selectedColorImage,
            image_link: previewColorImage
          });
        }
      } else {
        // TH sửa màu
        for (let i = 0; i < colors.length; i++) {
          colors[indexColor] = {
            _id: nameColor,
            name_vn: listColor.find((obj) => obj._id === nameColor).name_vn,
            amount: amountColor,
            price: priceColor,
            real_price: realPriceColor,
            image: selectedColorImage ? selectedColorImage : imageID,
            image_link: previewColorImage ? previewColorImage : imageColor
          };
        }
      }
    }
    this.setState({
      colors,
      // Gán giá trị button
      colorEditing: "none",
      btnStatus: "Thêm màu",
      // Gán giá trị fields
      nameColor: "",
      amountColor: 0,
      priceColor: 0,
      realPriceColor: 0,
      onEditing: false,
      imageColor: INITIAL_IMAGE,
    });
  }

  componentDidUpdate(props) {
    const { product, categoryDetail, listCategories } = this.props;
    if (categoryDetail !== props.categoryDetail && categoryDetail) {
      // categoryDetail thay doi
      if (product && categoryDetail._id === product.category._id) {
        // truong hop sua
        if (product.specifications.length > 0) {
          this.setState({
            specifications: this.setValue(
              product,
              listCategories[
                listCategories.findIndex((i) => i._id === product.category._id)
              ]
            ),
          });
        } else {
          this.setState({
            specifications: this.setValue(product, categoryDetail),
          });
        }
      } else {
        // truong hop them moi
        this.setState({
          specifications: this.setValue(product, categoryDetail),
        });
      }
    }
  }

  onSubmitImage = async () => {
    const { selectedFile, selectedList, id } = this.state;
    // @Condition cloudinary là FormData()
    // @Xử lý ảnh trước khi lưu
    // Nếu có import thumbnail mới thì
    if (selectedFile && selectedList.length !== 0) {
      var formData1 = new FormData();
      formData1.append("image", selectedFile);
      await this.setState({
        bigimage: formData1,
      });
      var formData2 = new FormData();
      for (var i = 0; i < selectedList.length; i++) {
        formData2.append("image", selectedList[i]);
      }
      this.onCallback(id, formData2);
    } else if (selectedFile) {
      // 1. Lưu cloudinary
      /* eslint-disable */
      var formData1 = new FormData();
      formData1.append("image", selectedFile);
      await this.setState({
        bigimage: formData1,
      });
      /* eslint-disable */
      this.onCallback(id);
    } else if (selectedList.length !== 0) {
      // 1. Lưu cloudinary
      // eslint-disable-next-line
      var formData2 = new FormData();
      for (i = 0; i < selectedList.length; i++) {
        formData2.append("image", selectedList[i]);
      }
      this.onCallback(id, formData2);
    } else {
      this.onCallback(id, null);
    }
  };

  onCallback = (id, formData) => {
    const { onCreate, onUpdateImage, queryParams } = this.props;
    const {
      name,
      price,
      amount,
      warrently,
      circumstance,
      included,
      category,
      brand,
      bigimage,
      weight,
      height,
      width,
      length,
      image,
      pathseo,
      specifications,
      colors,
      description,
      group
    } = this.state;
    if (id) {
      // eslint-disable-next-line
      var data = {
        name,
        price,
        amount,
        warrently,
        circumstance,
        included,
        category,
        brand,
        bigimage,
        weight,
        height,
        width,
        length,
        image,
        pathseo,
        specifications,
        colors,
        group: group && group._id ? group._id : group,
        description: JSON.stringify(
          convertToRaw(description.getCurrentContent())
        ),
        desc_text: description.getCurrentContent().getPlainText('\u0001')
      };
      onUpdateImage(id, data, formData, queryParams);
    } else {
      // 4. Create data
      // eslint-disable-next-line
      var data = {
        name,
        price,
        amount,
        warrently,
        circumstance,
        included,
        category,
        brand,
        bigimage,
        weight,
        height,
        width,
        length,
        pathseo,
        image,
        specifications,
        colors,
        group: group && group._id ? group._id : group,
        description: JSON.stringify(
          convertToRaw(description.getCurrentContent())
        ),
        desc_text: description.getCurrentContent().getPlainText('\u0001')
      };
      onCreate(data, formData, queryParams);
    }
  };

