import React, { Component } from 'react'
//Components
import {
  CButton,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
} from "@coreui/react";


export default class Images extends Component {
  constructor(props){
    super(props);

  }

  render() {
    const {modal, onCloseModal, image} = this.props;
    console.log(image)
    return (
      <CModal show={modal} onClose={() => onCloseModal(!modal)} closeOnBackdrop={false}>
        <CModalHeader closeButton>
          <CModalTitle>
            Danh sách hình ảnh
          </CModalTitle>
        </CModalHeader>
        <CModalBody>
          {image.map((item, index)=>{
            return (
              <button key={index} className="btn">
                <img src={item.public_url} className="img-thumbnail2"></img>
              </button>
            )
          })}
        </CModalBody>
      </CModal>
    )
  }
}
