import React, { Component }  from 'react'
import { connect } from "react-redux";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
// @Components
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CButton,
  CRow,
} from '@coreui/react'
// @Actions
import NotificationActions from "../../redux/actions/notification";

const fields = ['date', 'image', 'content',{ key: 'actions', _style: { width: '30%'} }]

class NotificationList extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }
  componentDidMount() {
    const { onClearState, onGetList } = this.props;
    onClearState();
    onGetList();
  }

  submit = (id) => {
    confirmAlert({
      title: 'Thông báo',
      message: 'Bạn có thực sự muốn xóa color này?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => this.onDelete(id)
        },
        {
          label: 'No'
        }
      ]
    });
  };
  handleListProduct = (id) =>{
    const { history } = this.props;
    const pathname = '/products/product-manage';
    history.push(`${pathname}?color=${id}`);
  }

  onDelete = (_id)=>{
    const {onDelete} = this.props;
    onDelete(_id);
  }

  onUpdate = (item) =>{
    const { onGetDetail } = this.props;
    if(item){
      onGetDetail(item)
    }
  }

  render () {
    const {listNotification} = this.props;
    return (
      <>
        <CRow>
          <CCol>
            <CCard>
              <CCardHeader>
                <h5 className="float-left my-2">Thông báo</h5>
                <CButton
                  onClick={() => this.onDeleteAllNoti()}
                  className="mb-1 float-right"
                  color="danger"
                > Xóa tất cả
                </CButton>
                <CButton
                  onClick={() => this.onReadAllNoti()}
                  className="mb-1 mr-2 float-right"
                  color="info"
                > Đọc tất cả
                </CButton>
              </CCardHeader>

              <CCardBody>
                <CDataTable
                  items={listNotification}
                  fields={fields}
                  hover
                  striped
                  bordered
                  itemsPerPage={10}
                  pagination
                  scopedSlots = {{
                    'date': (item) => (
                      <td>{new Date(item.createdAt).toLocaleDateString("vi-VN")}</td>
                    ),
                    'image': (item) => (
                      <td><img src={item.image.public_url} style={{width:'8vw'}} className="rounded-circle" alt={item.image._id}></img></td>
                    ),
                    'content': (item) => (
                      <td>
                        <p className="font-weight-bold">{item.name}</p>
                        <p>{item.content}</p>
                      </td>
                    ),
                    'actions':
                    (item)=>(
                      <td>
                        <CButton
                          onClick={() => this.handleListProduct(item._id)}
                          className="mr-1 mb-1 mb-xl-0"
                          color="success"
                        >
                          Danh sách
                        </CButton>
                        <CButton
                          onClick={() => this.onUpdate(item._id)}
                          className="mr-1 mb-1 mb-xl-0"
                          color="warning"
                        >
                          Sửa
                        </CButton>
                        <CButton
                          onClick={() => this.submit(item._id)}
                          className="mr-1"
                          color="danger"
                        >
                          Xóa
                        </CButton>
                      </td>)
                  }}
                />
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listNotification: state.notification.list,
    total : state.notification.total,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onGetList: (payload) => {
      dispatch(NotificationActions.onGetList(payload))
    },
    onClearState: () =>{
      dispatch(NotificationActions.onClearState())
    },
    onUpdate: (id, data, params) => {
      dispatch(NotificationActions.onUpdate(id, data, params))
    },
    onUpdateAll: (data, params) => {
      dispatch(NotificationActions.onUpdateAll(data, params))
    },
    onDelete: (id, params) => {
      dispatch(NotificationActions.onDelete(id, params))
    },
    onDeleteAll: (id, params) => {
      dispatch(NotificationActions.onDeleteAll(id, params))
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(NotificationList)
