import React, { Component } from 'react';
import { CButton, CModal, CModalBody, CModalFooter, CModalHeader, CModalTitle } from '@coreui/react';

class ProductDetail extends Component {
	render() {
		const { large, setLarge, detail } = this.props;
		return (
			<CModal show={large} onClose={() => setLarge(!large)} size="lg">
				<CModalHeader closeButton>
					<CModalTitle>{detail.name}</CModalTitle>
				</CModalHeader>
				<CModalBody>
					<div className="row">
						<div className="col-12 col-lg-6">
							<form onSubmit={this.onSubmit}>
								<div className="form-group">
									<label for="">Tên sản phẩm</label>
                  <input type="text" className="form-control" value={detail.name}/>
								</div>
								<div className="form-group">
									<label for="">Gía bán lẻ</label>
                  <input type="number" className="form-control" value={detail.price}/>
								</div>
							</form>
						</div>
					</div>
				</CModalBody>
				<CModalFooter>
					<CButton color="primary" onClick={() => setLarge(!large)}>
						Do Something
					</CButton>{' '}
					<CButton color="secondary" onClick={() => setLarge(!large)}>
						Cancel
					</CButton>
				</CModalFooter>
			</CModal>
		);
	}
}

export default ProductDetail;
