import React, { Component }  from 'react'
import { get } from "lodash";
import { connect } from "react-redux";
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CButton,
  CRow,
} from '@coreui/react'
import ProductsList from './ProductsList'
import ProductsActions from "../../redux/actions/products";
import ImagesActions from "../../redux/actions/cloudinary";
import BrandActions from "../../redux/actions/brands";

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}


class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      large: false,
    }
  }
  componentWillMount(){
    const { onGetListImage, onGetListBrand } = this.props;
    onGetListImage();
    onGetListBrand();
  }

  componentDidMount() {
    const { onGetList } = this.props;
    onGetList();
  }

  setLarge = (large, item) =>{
    const { onGetDetail } = this.props;
    this.setState({
      large
    })
    onGetDetail(item);
  }

  setImage = (image) => {
    const {listImages} = this.props;
    const img = listImages.find(obj => obj._id === image);
    return get(img, "public_url");
  }

  setBrand = (brand) =>{
    const {listBrands} = this.props;
    const brandName = listBrands.find(obj => obj._id === brand);
    return get(brandName, "name");
  }

  render () {
    const {large} = this.state;
    const {listProducts, productDetail, setLarge, setImage, setBrand} = this.props;

    return (
      <>
        <CRow>
          <CCol>
            <CCard>
              <CCardHeader>
                <h5 className="float-left my-2">Danh sách sản phẩm</h5>
                <CButton
                  onClick={() => setLarge(!large)}
                  className="mb-1 float-right"
                  color="success"
                > Thêm sản phẩm
                </CButton>
              </CCardHeader>
              <ProductsList large={large} listProducts={listProducts} productDetail={productDetail}
              setImage={setImage} setBrand={setBrand}/>
            </CCard>
          </CCol>
        </CRow>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listProducts: state.products.list,
    productDetail: state.products.detail,
    listImages: state.cloudinary.list,
    listBrands: state.brands.list,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onGetList: () => {
      dispatch(ProductsActions.onGetList())
    },
    onGetDetail: (id) => {
      dispatch(ProductsActions.onGetDetail(id))
    },
    onGetListImage: () => {
      dispatch(ImagesActions.onGetList())
    },
    onGetListBrand: () => {
      dispatch(BrandActions.onGetList())
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductList)
