import React, { Component } from 'react';
import { CButton, CModal, CModalBody, CModalFooter, CModalHeader, CModalTitle, CImg } from '@coreui/react';
import { connect } from "react-redux";
// @Actions
import ColorActions from "../../redux/actions/color";
// @Functions
import {INITIAL_IMAGE} from '../../constants';

class InstallmentDetail extends Component {
  constructor(props){
    super(props);
    const {installment} = props;
    this.state = {
      id: installment ? installment._id : '',
      startedAt: installment ? installment.startedAt : '',
      endedAt: installment ? installment.endedAt : '',
      period: installment ? installment.period : '',
      interest_rate: installment ? installment.interest_rate : '',
      paid: installment ? installment.paid : 0,
      product: installment ? installment.product : {},
      status: installment ? installment.status : -1,
      staff: installment ? installment.staff : '',
      user: installment ? installment.user : '',
      detail: installment ? installment._detail : [],
    }
  }
  onChange = (event) =>{
    var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
        [name]: value
    })
  }

  onSubmit = () =>{
    const {id, name_vn, name_en, code} = this.state;
    const {onUpdate, onCreate} = this.props;
    var data = {name_vn, name_en, code}
    if (id) {
      onUpdate(id, data);
    }
    else {
      // 4. Create data
      onCreate(data);
    }
  }

  handleChangeComplete = (color) => {
    this.setState({ code: color.hex });
  };

	render() {
    const { id, startedAt, endedAt, period, interest_rate, paid, product, status, staff, user, detail } = this.state;
    const { large, onClose, installment } = this.props;
    return (
			<CModal show={large} onClose={() => onClose(!large)} size="lg">
				<CModalHeader closeButton>
        <CModalTitle>{installment ? "Sửa thông tin phiếu trả góp" : "Thêm phiếu trả góp"}</CModalTitle>
				</CModalHeader>
				<CModalBody>
        <div className="row">
						<div className="col-12 col-lg-6">
              <div className="form-group">
                <label>Mã phiếu:</label>
                <input type="text" className="form-control" name="id" value={id} disabled readOnly/>
              </div>
              <div className="form-group">
                <label>Khách hàng:</label>
                <div className="row rounded border">
                  <div className="col-12 c-avatar">
                    <CImg
                      src={user.image ? user.image.public_url : INITIAL_IMAGE}
                      className="c-avatar-img"
                      alt={user._id}
                    />
                    <p className="mb-0">{user.firstname} {user.lastname}</p>
                  </div>
                </div>
              </div>
              <div className="form-group">
                <label>Sản phẩm trả góp:</label>

              </div>
              <div className="form-group">
                <label>Trả trước:</label>

              </div>
              <div className="form-group">
                <label>Lãi xuất:</label>

              </div>
              <div className="form-group">
                <label>Kỳ hạn: (tháng)</label>
                {/* <input type="text" className="form-control" name="name_vn" value={name_vn} onChange={this.onChange}/> */}
              </div>
            </div>
            <div className="col-12 col-lg-6">
              <div className="form-group">
                <label>Tình trạng trả góp:</label>

              </div>
              <div className="form-group">
                <label>Ngày lập phiếu:</label>

              </div>
              <div className="form-group">
                <label>Ngày đáo hạn:</label>

              </div>
              <div className="form-group">
                <label>Lịch sử trả góp:</label>

              </div>
              <div className="form-group">
                <label>Số tiền còn nợ:</label>

              </div>
            </div>
					</div>
				</CModalBody>
				<CModalFooter>
					<CButton color="primary" onClick={() => this.onSubmit(!large)}>
						Save
					</CButton>{' '}
					<CButton color="secondary" onClick={() => onClose(!large)}>
						Cancel
					</CButton>
				</CModalFooter>
			</CModal>
		);
	}
}

const mapStateToProps = (state) => {
  return {
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onCreate: (params) =>{
      dispatch(ColorActions.onCreate({params}))
    },
    onUpdate: (id, params) =>{
      dispatch(ColorActions.onUpdate({id, params}))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(InstallmentDetail);
