import React, { Component }  from 'react'
import { connect } from "react-redux";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
// @Components
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CButton,
  CRow,
} from '@coreui/react'
// @Actions
import NotificationActions from "../../redux/actions/notification";

const fields = ['name_vn', 'name_en', 'color',{ key: 'actions', _style: { width: '30%'} }]

class NotificationList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      large: false,
    }
  }
  componentDidMount() {
    const { onClearState, onGetList } = this.props;
    onClearState();
    onGetList();
  }

  setLarge = (large) => {
    this.setState({
      large
    })
  }

  submit = (id) => {
    confirmAlert({
      title: 'Thông báo',
      message: 'Bạn có thực sự muốn xóa color này?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => this.onDelete(id)
        },
        {
          label: 'No'
        }
      ]
    });
  };
  handleListProduct = (id) =>{
    const { history } = this.props;
    const pathname = '/products/product-manage';
    history.push(`${pathname}?color=${id}`);
  }

  onDelete = (_id)=>{
    const {onDelete} = this.props;
    onDelete(_id);
  }

  onUpdate = (large, item) =>{
    const { onGetDetail } = this.props;
    this.setState({
      large
    })
    if(item){onGetDetail(item)}
  }

  onClose = (large) =>{
    const { onClearDetail } = this.props;
    this.setState({
      large
    })
    onClearDetail();
  }

  render () {
    const {large} = this.state;
    const {listColor, onClearDetail} = this.props;
    return (
      <>
        <CRow>
          <CCol>
            <CCard>
              <CCardHeader>
                <h5 className="float-left my-2">Thông báo</h5>
                <CButton
                  onClick={() => this.onDeleteAllNoti()}
                  className="mb-1 float-right"
                  color="danger"
                > Xóa tất cả
                </CButton>
                <CButton
                  onClick={() => this.onReadAllNoti()}
                  className="mb-1 float-right"
                  color="warning"
                > Đọc tất cả
                </CButton>
              </CCardHeader>

              <CCardBody>
                <CDataTable
                  items={listColor}
                  fields={fields}
                  hover
                  striped
                  bordered
                  itemsPerPage={10}
                  pagination
                  scopedSlots = {{
                    'name_vn': (item) => (
                      <td>{item.name_vn}</td>
                    ),
                    'name_en': (item) => (
                      <td>{item.name_en}</td>
                    ),
                    'color': (item) => (
                      <td><CRow><CCol xl="2" md="4" sm="6" xs="12" className="mb-4" style={{paddingTop: '0',height:'25px', backgroundColor: item.code}}>
                    </CCol></CRow></td>
                    ),
                    'actions':
                    (item)=>(
                      <td>
                        <CButton
                          onClick={() => this.handleListProduct(item._id)}
                          className="mr-1 mb-1 mb-xl-0"
                          color="success"
                        >
                          Danh sách
                        </CButton>
                        <CButton
                          onClick={() => this.onUpdate(!large, item._id)}
                          className="mr-1 mb-1 mb-xl-0"
                          color="warning"
                        >
                          Sửa
                        </CButton>
                        <CButton
                          onClick={() => this.submit(item._id)}
                          className="mr-1"
                          color="danger"
                        >
                          Xóa
                        </CButton>
                      </td>)
                  }}
                />
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listColor: state.color.list,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onGetList: () => {
      dispatch(NotificationActions.onGetList())
    },
    onClearState: () =>{
      dispatch(NotificationActions.onClearState())
    },
    onClearDetail: () =>{
      dispatch(NotificationActions.onClearDetail())
    },
    onGetDetail: (id) => {
      dispatch(NotificationActions.onGetDetail(id))
    },
    onDelete: (id) =>{
      dispatch(NotificationActions.onDelete({id}))
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(NotificationList)
