import { SelectorActionTypes } from "../actions/selector";
import { toastError, toastSuccess } from "../../utils/toastHelper";

const init = {
  loading: true,
  detail: null,
  processing: false,
};

export default function (state = init, action) {
  switch (action.type) {
    case SelectorActionTypes.CREATE:
    case SelectorActionTypes.DELETE:
      return {
        ...state,
        processing: true,
      };

    case SelectorActionTypes.CREATE_ERROR:
    case SelectorActionTypes.DELETE_ERROR:
      var { message } = action.payload;
      toastError(message);
      return {
        ...state,
        processing: false,
      };
    case SelectorActionTypes.CREATE_SUCCESS:
      toastSuccess("Tạo mới thành công");
      return {
        ...state,
        processing: true,
      };
    case SelectorActionTypes.DELETE_SUCCESS:
      toastSuccess("Xóa thành công");
      return {
        ...state,
        processing: false,
      };
    default:
      return state;
  }
}
