import React, { Component } from 'react'
import { connect } from "react-redux";
// @Components
import {
  CButton,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
  CDataTable
} from "@coreui/react";
// @Actions
import ProductsActions from "../../redux/actions/products";
// @Functions
import {INITIAL_IMAGE} from '../../constants';

const fields = ['ảnh', 'tên hiển thị', 'tên thật', { key: 'actions', _style: { width: '15%'} }]

class Group extends Component {
  constructor(props){
    super(props);
    const {group} = props
    this.state = {
      keyword: "",
      listProduct: group.products ? group.products : []
    }
  }

  handleFilter = (event) => {
    const { keyword } = this.state;
    const { onFilter } = this.props;
		var target=event.target;
    var name=target.name;
    var value=target.value;
    this.setState({
      [name]:  value
    })
    onFilter(keyword);
  }

  onAdd = (product) =>{
    const {listProduct} = this.state;
    console.log(listProduct)
    console.log(product)
    listProduct.push(product);
/*     this.setState({
      listProduct,
    }) */
  }

  onDelete = (item) =>{
    const {listProduct} = this.state;
    listProduct.splice(listProduct.indexOf(item), 1);
    this.setState({
      listProduct
    })
  }

  render() {
    const {modal, onCloseModal, group, listSearch} = this.props;
    const {keyword, listProduct} = this.state;
    return (
      <CModal show={modal} onClose={() => onCloseModal("_modal",!modal)} closeOnBackdrop={false} size="lg">
        <CModalHeader closeButton>
          <CModalTitle>
            Nhóm sản phẩm {group._id}
          </CModalTitle>
        </CModalHeader>
        <CModalBody>
          <div className="row">
          <div className="col-12 col-md-6">
            <input className="form-control" name="keyword" value={keyword} onChange={this.handleFilter}></input>
            <div className="card mb-0">
            {listSearch && keyword && listSearch.map((product, index) =>{
              return (
                <div key={index}>
                  <div className="row text-dark text-decoration-none" style={{height: "80px"}} key={index}>
                    <div className="col-3 my-auto">
                      <><img style={{height: "80px"}} src={product.bigimage.public_url}></img></>
                    </div>
                    <div className="col-7 text-left my-auto">
                      <p className="mb-0">{product.name}</p>
                      <p className="mb-0 font-italic">{product.price_min} VND</p>
                    </div>
                    <div className="col-2">
                      <button type="button" className="btn btn-success" onClick={() => this.onAdd(product)}><i className="fa fa-folder-plus"></i></button>
                    </div>
                  </div>
                  <div className="border-bottom"></div>
                </div>
              )
            })}
          </div>
          </div><div className="col-12 col-md-6">
            {listProduct && <CDataTable
              items={listProduct}
              fields={fields}
              striped
              itemsPerPage={5}
              pagination
              scopedSlots = {{
                'ảnh':
                  (item) => (
                    <td>
                      <img src={ item.product.bigimage ? item.product.bigimage.public_url : INITIAL_IMAGE } style={{height:'10vh'}} alt={item.product.name} />
                    </td>
                  ),
                'tên hiển thị': (item) => (
                  <td>{item.name}</td>
                ),
                'tên thật': (item) => (
                  <td>{item.product.name}</td>
                ),
                'actions': (item) => (
                  <td>
                  <CButton
                    /* onClick={() => this.onDeleteSpecification(item)} */
                    className="mr-1 mb-1 mb-xl-0"
                    color="warning"
                  >
                    <i className="fa fa-highlighter"></i>
                  </CButton>
                  <CButton
                    onClick={() => this.onDelete(item)}
                    className="mr-1 mb-1 mb-xl-0"
                    color="danger"
                  >
                    <i className="fa fa-trash-alt"></i>
                  </CButton>
                  </td>
                )
              }}
            />}
          </div>
          </div>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary" /* onClick={() => onCloseModal("_modal",!modal)} */>
            Save group
          </CButton>{" "}
          <CButton color="secondary" onClick={() => onCloseModal("_modal",!modal)}>
            Cancel
          </CButton>
        </CModalFooter>
      </CModal>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    listSearch: state.products.listSearch,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFilter : (keyword) =>{
      dispatch(ProductsActions.onFilter(keyword))
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Group);
