import React, { Component } from 'react';
import { CButton, CModal, CModalBody, CModalFooter, CModalHeader, CModalTitle } from '@coreui/react';
import { connect } from "react-redux";
import ProductsActions from "../../redux/actions/products";

class ProductDetail extends Component {
  constructor(props) {
    super(props);
    this.state({
      product: null
    })
  }

  componentDidMount() {
    const { onGetDetail, detail, productDetail } = this.props;
    onGetDetail(detail);
    this.setState({
      product: productDetail
    })
  }

	render() {
    const { large, setLarge } = this.props;
    const {product} = this.state;
		return (
			<CModal show={large} onClose={() => setLarge(!large)} size="lg">
				<CModalHeader closeButton>
        <CModalTitle>{product.name}</CModalTitle>
				</CModalHeader>
				<CModalBody>
					<div className="row">
						<div className="col-12 col-lg-6">
							<form onSubmit={this.onSubmit}>
								<div className="form-group">
									<label for="">Tên sản phẩm</label>
                  <input type="text" className="form-control" name="name" value={null}/>
								</div>
								<div className="form-group">
									<label for="">Gía bán lẻ</label>
                  <input type="number" className="form-control" name="price" value={null}/>
								</div>
							</form>
						</div>
					</div>
				</CModalBody>
				<CModalFooter>
					<CButton color="primary" onClick={() => setLarge(!large)}>
						Do Something
					</CButton>{' '}
					<CButton color="secondary" onClick={() => setLarge(!large)}>
						Cancel
					</CButton>
				</CModalFooter>
			</CModal>
		);
	}
}

const mapStateToProps = (state) => {
  return {
    productDetail: state.products.detail
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onGetDetail: (id) => {
      dispatch(ProductsActions.onGetDetail(id))
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps) (ProductDetail);
